    {id:2, title:'javascript', stack:'mern stack'},
    {id:3, title:'Beckend', stack:'middle'},
    {id:4, title:'cyber', stack:'senior'},
    {id:5, title:'frontend', stack:'junior'}
  ]
)

const [post, setPost]=useState({title:'', stack:''})

