import '.style/'

function App() {

 const  [count, setCount]=useState(0)

function inc(){
  setCount(count+1)
}
function dec(){
  setCount(count-1)
}

  return (
    <>
  <div className="app w-50 mx-auto">
    <h1 className="">Count {count}</h1>
    <button onClick={inc} className="btn btn-success">incr</button>
    <button onClick={dec} className="btn btn-danger">Decr</button>

  </div>


  </>
  );
}

export default App;
